import React, { useState } from 'react';
import './Register.css'; // Import the CSS file for styling

const Register = ({ toggleForm }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');

  const handleSignUp = async (e) => {
    e.preventDefault();

    // Handle registration logic (e.g., API call to register the user)
    console.log('Registering user:', { name, email, phone });
  };

  return (
    <div className="container">
      <div className="register-container">
        <div className="image" data-tilt></div>
        <div className="info">
          <form className="register-form" onSubmit={handleSignUp}>
            <span className="heading"><h1>Sign Up</h1></span>
            <div className="validate-input">
              <input
                className="input"
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
              <i className="fa fa-user"></i>
            </div>
            <div className="validate-input">
              <input
                className="input"
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <i className="fa fa-envelope"></i>
            </div>
            <div className="validate-input">
              <input
                className="input"
                type="tel"
                placeholder="Phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                required
              />
              <i className="fa fa-phone"></i>
            </div>
            <div className="register-btn">
              <button className="btn" type="submit">Sign Up</button>
            </div>
          </form>
          <div className="toggle-text" onClick={toggleForm}>
            ...or <span className="highlighted">Sign in</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Register;
