import React, { useState } from 'react';
import './design.css'; // Import your CSS file for styling

const Login = ({ toggleForm }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        console.log('Login successful', data);
      } else {
        console.log('Login failed', data.message);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container">
      <div className="login-container">
        <div className="image" data-tilt></div>
        <div className="info">
          <form action="" className="login-form" onSubmit={handleLogin}>
            <span className="heading"><h1>Member login</h1></span>
            <div className="validate-input" data-validate="Enter a valid Email">
              <input 
                className="input" 
                type="email" 
                name="email" 
                placeholder="Email" 
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
              />
              <i className="fa fa-envelope"></i>
            </div>
            <div className="validate-input" data-validate="Password is required">
              <input 
                className="input" 
                type="password" 
                name="pass" 
                placeholder="Password" 
                value={password} 
                onChange={(e) => setPassword(e.target.value)} 
              />
              <span>
                <i className="fa fa-lock"></i>
              </span>
            </div>

            <div className="login-btn">
              <button className="btn" type="submit">Login</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
