import React, { useState } from 'react';
import Login from './pages/Login/Login';
import Register from './pages/Register/Register';

const App = () => {
  const [isRegistering, setIsRegistering] = useState(true);

  // Define the image URLs for the Register and Login forms
  const registerImage = 'https://colorlib.com/etc/lf/Login_v1/images/img-01.png';
  const loginImage = 'https://colorlib.com/etc/lf/Login_v1/images/img-01.png';

  // Determine the current image based on the form state
  const currentImage = isRegistering ? registerImage : loginImage;

  return (
    <div className="container">
      {isRegistering ? (
        <Register
          onSignIn={() => setIsRegistering(false)}
          image={currentImage} // Pass the image URL to Register
        />
      ) : (
        <Login
          onSignUp={() => setIsRegistering(true)}
          image={currentImage} // Pass the image URL to Login
        />
      )}
    </div>
  );
}

export default App;
