import React, { useState } from 'react';
import './Register.css'; // Import your CSS file for styling

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, phone }),
      });

      const data = await response.json();

      if (response.ok) {
        // Handle successful registration (e.g., save token, redirect)
        console.log('Registration successful', data);
      } else {
        // Handle errors (e.g., display error message)
        console.log('Registration failed', data.message);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container">
      <div className="register-container">
        <div className="info">
          <form action="" className="register-form" onSubmit={handleRegister}>
            <span className="heading"><h1>Sign Up</h1></span>
            <div className="validate-input" data-validate="Name is required">
              <input 
                className="input" 
                type="text" 
                name="name" 
                placeholder="Name" 
                value={name} 
                onChange={(e) => setName(e.target.value)} 
              />
              <i className="fa fa-user"></i>
            </div>
            <div className="validate-input" data-validate="Enter a valid Email">
              <input 
                className="input" 
                type="email" 
                name="email" 
                placeholder="Email" 
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
              />
              <i className="fa fa-envelope"></i>
            </div>
            <div className="validate-input" data-validate="Phone is required">
              <input 
                className="input" 
                type="text" 
                name="phone" 
                placeholder="Phone" 
                value={phone} 
                onChange={(e) => setPhone(e.target.value)} 
              />
              <i className="fa fa-phone"></i>
            </div>

            <div className="signup-btn">
              <button className="btn" type="submit">Sign Up</button>
            </div>
          </form>
        </div>
        <div className="image"></div>
      </div>
    </div>
  );
}

export default Register;
